ll = [1,2,3]d = dict(a=1)print(type(ll))print(type(d))print(isinstance(ll,list))print(isinstance(d,dict))def add(a,b):    if isinstance(a,int):        return  a + b    elif isinstance(a,str):        return a.upper() + bprint(add(1,2))print(add('head','tail'))#id()获取变量的内存地址，十进制表示print(id(ll))print(id(d))print(ll is d)print(ll is ll)#== 和 is 的区别ll1 = [1,2,3]ll2 = [1,2,3]print(ll1 == ll2) #Tureprint(ll1 is ll2) #False#所以判断对象是不是None的时候，一般用is，None是单例对象，内存地址是唯一的