import socketimport timeif __name__ == '__main__':    client = socket.socket(family=socket.AF_INET,type=socket.SOCK_STREAM)    client.connect(('127.0.0.1',8080))    #    str = 'Hello World2'    client.sendall(str.encode())    recvs = client.recv(1024)    print('client received -> {0}'.format(recvs.decode()))    #    tm = time.ctime(time.time())    client.sendall(tm.encode())    recvs = client.recv(1024)    print('client received -> {0}'.format(recvs.decode()))    client.close()