import socket#建立一个服务器import timeserver = socket.socket(family=socket.AF_INET,type=socket.SOCK_STREAM)server.bind(('127.0.0.1',8888))#绑定监听端口server.listen()#开始监听，参数表示可以使用几个链接排队def a():    while True:#客户端连接过来，生成服务端的连接实例        conn,addr = server.accept()#等待连接，返回两个值        print('addr->{0} connected!'.format(addr))        try:            data = conn.recv(1024)#接受数据            print('received data->{0}'.format(data.decode()))            tm = time.ctime(time.time())#发送数据            conn.sendall(tm.encode())        except ConnectionResetError as e:            print('关闭了正在占线的连接！')            break        conn.close()if __name__ == '__main__':    a()