# -*- coding: UTF-8 -*-# 从文件中读取内容def find_answer(question):    with open('reply.txt','r',encoding='gbk') as f:        while True:            line = f.readline()            print(line)            if not line:                break            keyword = line.split('|')[0]            reply = line.split('|')[1]            if keyword in question:                return reply    return Falseif __name__ == '__main__':    # 输入问题    question = input('HI,你好')    while True:        print(question)        if question == 'bye':            break        reply = find_answer(question)        if not reply:            question = input('我真的不知道你再说什么')        else:            print(reply)            question = input('您还有什么问题吗？结束请输入bye')    print('再见')    # lines = []    # while True:    #     str = input('请输入')    #     if str == '0':    #         break    #     lines.append(str)    # print(lines)