# -*- coding: UTF-8 -*-# 创建实例对象的执行过程class Person(object):    def __new__(cls, *args, **kwargs):        print('__new__被调用执行了，cls的 id 值为: %s' % id(cls))        obj = super().__new__(cls)#obj为创建的对象        print('__创建的对象的id值为: %s ' % id(obj))        return obj        # if not hasattr(cls,'_instance'):        #     cls._instance = super().__new__(cls)        # return cls._instance    def __init__(self,name,age):        print('__init__被调用执行了，self的id值为: %s ' % id(self))        self.name = name# 实例属性        self.age = ageprint('object这个类对象的id值为: %s ' %id(object))print('Person这个类对象的id值为: %s ' %id(Person))p1 = Person('zhang3',18)print('p1这个实例对象的id值为: %s ' %id(p1))