# -*- coding: UTF-8 -*-# 封装# 私有属性是通过__表示的class Person():    def __init__(self,name,age):        self.name = name        #self.__age = age # 实例对象的私有属性        self.set_age(age)    def show(self):        print(self.name,self.__age)    def get_age(self):        return self.__age    def set_age(self,age):        if 0<=age<=120:            self.__age = age        else:            raise ValueError('年龄复赋值不正确')if __name__ == '__main__':    p1 = Person('zhang3',18)    print(p1.name)    # print(p1.__age) # 不能直接访问    # print(p1._Person__age) # 可以间接访问    p1.show()    print(dir(p1))