# -*- coding: UTF-8 -*-from selenium import webdriverimport timeimport requestsfrom requests.cookies import RequestsCookieJarurl = "https://fanyi.baidu.com"# url = "http://fanyi.baidu.com/v2transapi"def login(url):    url = url    res = requests.get(url)    # print(res.cookies)    # print(type(res.cookies))    # print(res.cookies.items())    cookies = res.cookies    cookie = requests.utils.dict_from_cookiejar(cookies)    return cookiedef trans(d):    res = []    for item in d.items():         res.append('='.join(item))    return ';'.join(res)def get_data(url):    cookie = login(url)    print(cookie)    cookieStrs = trans(cookie)    headers = {    "cookie": cookieStrs    }    res = requests.get(url=url, cookies=cookie)    # res = requests.get(url=url, headers=headers)    print(res.status_code)    cookies = res.cookies    cookie = requests.utils.dict_from_cookiejar(cookies)    print(cookie)get_data(url)print('-'*30)# url2 = "http://fanyi.baidu.com/v2transapi"## cookie_jar = RequestsCookieJar()## cookie_jar.set("BAIDUID", "B1CCDD4B4BC886BF99364C72C8AE1C01:FG=1", domain="baidu.com")### res = requests.get(url, cookies=cookie_jar)## print(res.status_code)## print('-'*30)